/*Table structure for table `tbl_day` */

DROP TABLE IF EXISTS `tbl_day`;

CREATE TABLE `tbl_day` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `day` tinyint(2) unsigned zerofill NOT NULL,
  `month` tinyint(2) unsigned zerofill DEFAULT NULL,
  `year` smallint(4) unsigned zerofill DEFAULT NULL,
  `title` varchar(250) NOT NULL,
  `slug` varchar(250) NOT NULL,
  `content` text NOT NULL,
  `preview` varchar(1000) DEFAULT NULL,
  `username` varchar(250) NOT NULL,
  `email` varchar(250) NOT NULL,
  `location` varchar(100) DEFAULT NULL,
  `sanitize` varchar(1500) NOT NULL,
  `edit_code` int(10) DEFAULT NULL,
  `notify` varchar(5) DEFAULT NULL,
  `photos` varchar(2000) NOT NULL DEFAULT '',
  `like` int(10) unsigned DEFAULT '0',
  `like_ip` text NOT NULL,
  `count` int(10) DEFAULT '0',
  `date` datetime DEFAULT NULL,
  `time` int(10) unsigned DEFAULT NULL,
  `ipaddress` varchar(50) DEFAULT NULL,
  `session_id` varchar(50) DEFAULT NULL,
  `fb` varchar(10) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `title` (`title`),
  KEY `location` (`location`),
  KEY `day` (`day`),
  KEY `month` (`month`),
  KEY `year` (`year`),
  KEY `username` (`username`),
  KEY `preview` (`preview`(333)),
  KEY `sanitize` (`sanitize`(333))
) ENGINE=MyISAM AUTO_INCREMENT=100164 DEFAULT CHARSET=utf8;

